const array = [1,2,3,22,3,2,3,4]

function deleteRepat(array) {
    let dict = {}
    for(let index = 0; index < array.length; index++) {
        dict[array[index]] = index
    }
    return Object.keys(dict)
}

console.log(Object.keys(dict))
